if (!chats.has(chatContextKey)) {
    chats.set(chatContextKey, true);
    global.set('chats', Array.from(chats.entries()));
}

var helpMessage = "\n";
helpMessage += "\nYou are welcome: <strong>" + msg.originalMessage.from.username + "</strong>";
helpMessage += "\nThis Bot will allow you to manage the entire <strong>Smart Agriculture - Field Monitoring System</strong> project simulation."
helpMessage += "\n";
helpMessage += "\nYour chat id is <strong>" + chatId + "</strong>";
helpMessage += "\n";
helpMessage += chats.has(chatContextKey) && chats.get(chatContextKey) ? "\nAlerts have been <strong>activated</strong> for this chat!" : "\nAlerts have been <strong>deactivated</strong> for this chat!";
helpMessage += "\n";

helpMessage += "\nHere is a short list of commands:";
helpMessage += "\n/help - Shows Help Messagge.";
helpMessage += "\n/set_simulation - Set all simumaltion parameters.";
helpMessage += "\n/update_simulation_condition - Update the simulation condition."
helpMessage += "\n/current_configuration - Gets the current configuration of the sensor simulator."
helpMessage += "\n/toggle_alert - Enable or disable the reception of alerts."

helpMessage += "\n";



msg.payload.content = helpMessage;
msg.payload.options = {
    parse_mode: 'HTML',
    reply_parameters: {
        message_id: msg.payload.messageId
    }
}
return msg;

