function getChatNumbersWithTrue(chats) {
    return chats
        .filter(chat => chat[1] === true)
        .map(chat => parseInt(chat[0].split('_')[1]));
}
function groupAlertsByField(alerts) {
    // Creare un oggetto per raggruppare gli alert per fieldId
    const groupedFields = {};

    alerts.forEach(alert => {
        const { fieldId, sensorType, value, timestamp, threshold } = alert;

        // Se il fieldId non esiste gi√†, inizializzalo
        if (!groupedFields[fieldId]) {
            groupedFields[fieldId] = {
                fieldId: fieldId,
                alerts: []
            };
        }

        // Aggiungi l'alert al campo corrispondente
        groupedFields[fieldId].alerts.push({
            sensorType,
            value,
            timestamp,
            threshold
        });
    });
    return Object.values(groupedFields);
}

function getThresholdEmoji(threshold) {
    if (threshold.toLowerCase().includes('min')) {
        return '‚¨áÔ∏è'; // Soglia minima
    } else if (threshold.toLowerCase().includes('max')) {
        return '‚¨ÜÔ∏è'; // Soglia massima
    } else {
        return '‚ö†Ô∏è'; // Soglia generica
    }
}

function getUnit(sensorType) {
    switch (sensorType) {
        case 'ph':
            return '';
        case 'salinity':
            return 'ŒºS/cm';
        case 'soilMoisture':
            return '%';
        case 'temperature':
            return '¬∞C';
        case 'humidity':
            return '%';
        case 'rain':
            return 'mm';
        default:
            return '';
    }
}

