/***************************************************************
 * buildDashboardForField
 * Costruisce la singola dashboard (JSON) per il field specifico,
 * con i pannelli corrispondenti solo ai sensori "true".
 ***************************************************************/
function buildDashboardForField(fieldId, sensors, refreshValue, thresholds) {
    // Struttura base
    let baseDashboard = {
      "dashboard": {
        "annotations": {
          "list": [
            {
              "builtIn": 1,
              "datasource": {
                "type": "grafana",
                "uid": "-- Grafana --"
              },
              "enable": true,
              "hide": true,
              "iconColor": "rgba(0, 211, 255, 1)",
              "name": "Annotations & Alerts",
              "type": "dashboard"
            }
          ]
        },
        "editable": true,
        "fiscalYearStartMonth": 0,
        "graphTooltip": 0,
        "links": [],
        "panels": [],
        "preload": false,
        "refresh": refreshValue,         // es. "5s"
        "schemaVersion": 1,
        "tags": [],
        "templating": { "list": [] },
        "time": { "from": "now-15m", "to": "now" },
        "timepicker": {},
        "timezone": "browser",
        "title": `IoT Sensors Dashboard - Field ${fieldId}`,
        "uid": `iot-sensors-00${fieldId}`,  // uid univoco
        "version": 31, // o 1, come preferisci
        "weekStart": ""
      },
      "folderUid": "SA-FMS",
      "isFolder": false,
      "overwrite": true
    };

    // (1) Row "Overview"
    baseDashboard.dashboard.panels.push(buildOverviewRow());
    // (2) Text panel
    baseDashboard.dashboard.panels.push(buildTextPanel(fieldId, sensors));
    // (3) All Alerts row
    const alertsPanels = buildAllAlertsRow(fieldId);
    baseDashboard.dashboard.panels.push(...alertsPanels);

    // Aggiunge i blocchi sensori *solo* se sensors.xxx = true
    if (sensors.temperature) {
        baseDashboard.dashboard.panels.push(...buildTemperaturePanels(fieldId, thresholds.temperature));
    }
    if (sensors.humidity) {
      baseDashboard.dashboard.panels.push(...buildHumidityPanels(fieldId, thresholds.humidity));
    }
    if (sensors.soilMoisture) {
      baseDashboard.dashboard.panels.push(...buildSoilMoisturePanels(fieldId, thresholds.soilMoisture));
    }
    if (sensors.ph) {
      baseDashboard.dashboard.panels.push(...buildPhPanels(fieldId, thresholds.ph));
    }
    if (sensors.salinity) {
      baseDashboard.dashboard.panels.push(...buildSalinityPanels(fieldId, thresholds.salinity));
    }
    if (sensors.rain) {
      baseDashboard.dashboard.panels.push(...buildRainPanels(fieldId, thresholds.rain));
    }

    return baseDashboard;
}

