// ----- FUNZIONE DI UTILIT√Ä PER FARE LE SOSTITUZIONI -----
function replaceFieldReferences(templateStr, fieldId) {
    // Sostituzioni necessarie:
    // - "field1" -> "field<fieldId>"
    // - "Field 1" -> "Field <fieldId>"
    // - "\"Field\": \"1\"" -> "\"Field\": \"<fieldId>\""

    let out = templateStr;

    // Attenzione all'ordine delle sostituzioni.
    // 1) Sostituisco "field1" con "fieldX"
    out = out.replaceAll("field1", "field" + fieldId);

    // 2) Sostituisco "Field 1" con "Field X"
    out = out.replaceAll("Field 1", "Field " + fieldId);

    // 3) Sostituisco "Field": "1" con "Field": "X"
    out = out.replaceAll("\"Field\": \"1\"", `"Field": "${fieldId}"`);

    // 2) Sostituisco "Field 1" con "Field X"
    out = out.replaceAll("iot-sensors-001", "iot-sensors-00" + fieldId);

    return out;
}

// ----- CREAZIONE DEGLI ALERT IN BASE ALLA CONFIGURAZIONE -----
let alerts = [];

if (config && config.fields && Array.isArray(config.fields)) {
    for (let f of config.fields) {
        let fid = f.fieldId;         // es. 1, 2, 3, ...
        let sensors = f.sensors;     // oggetto con booleans per i sensori

        // Per ogni sensore nel template, se risulta true in config, duplico e modifico il template
        for (let sensorKey in sensors) {
            if (sensors[sensorKey] === true) {
                // Prendo il template corrispondente
                let sensorTemplate = templates[sensorKey];
                if (sensorTemplate) {
                    // Faccio la sostituzione
                    let replaced = replaceFieldReferences(sensorTemplate, fid);

                    // Converto in JSON
                    let alertObj = JSON.parse(replaced);

                    // Aggiungo all'array finale
                    alerts.push(alertObj);
                }
            }
        }
    }
}

// Metto l'array di alert in msg.payload
let thresholds = {
    "soilMoisture": {
        "min": 20,
        "max": 80
    },
    "temperature": {
        "min": 0,
        "max": 35
    },
    "ph": {
        "min": 5.5,
        "max": 7.5
    },
    "salinity": {
        "min": 200,
        "max": 1500
    },
    "humidity": {
        "min": 50,
        "max": 90
    },
    "rain": {
        "max": 40
    }
};


const thresholdConfig = global.get("sensorsThreshold") || thresholds;

for (let i = 0; i < alerts.length; i++) {
    let alert = alerts[i];

    const uid = alert?.uid;
    if (!uid) {

        continue;
    }


    const parts = uid.split("-");
    if (parts.length < 2) {

        continue;
    }
    const sensorType = parts[0];

    const sensorThreshold = thresholdConfig[sensorType];
    if (!sensorThreshold) {
        continue;
    }

    let cData = alert.data.find(d => d.refId === "C");
    if (!cData || !cData.model || !cData.model.conditions || !cData.model.conditions[0]) {

        continue;
    }

    let condition = cData.model.conditions[0];
    let evaluator = condition.evaluator;
    let unloadEval = condition.unloadEvaluator; 


    if (sensorThreshold.min !== undefined && sensorThreshold.max !== undefined) {

        evaluator.params = [sensorThreshold.min, sensorThreshold.max];
        if (unloadEval) {
            unloadEval.params = [sensorThreshold.min, sensorThreshold.max];
        }
    } else if (sensorThreshold.max !== undefined) {

        evaluator.params = [sensorThreshold.max];
        if (unloadEval) {
            unloadEval.params = [sensorThreshold.max];
        }
    }

}

// Ritorno l'array modificato in msg.payload
msg.payload = alerts;
return msg;

