  "soilMoisture": {
    "min": 20,
    "max": 80
  },
  "temperature": {
    "min": 0,
    "max": 35
  },
  "ph": {
    "min": 5.5,
    "max": 7.5
  },
  "salinity": {
    "min": 200,
    "max": 1500
  },
  "humidity": {
    "min": 50,
    "max": 90
  },
  "rain": {
    "max": 40
  }
};

const thresholdConfig = global.get("sensorsThreshold") || thresholds;

// Converte interval (ms) in stringa "Xs"
let refreshStr = convertIntervalToRefresh(input.interval);

// Crea un array di dashboards
let dashboards = [];

for (let f of input.fields) {
  let fieldId = f.fieldId;
  let sensors = f.sensors;
  // Crea una dashboard per questo field
  let dash = buildDashboardForField(fieldId, sensors, refreshStr, thresholdConfig);
  dashboards.push(dash);
}

msg.payload = dashboards;
return msg;

