        6: "2400",
        5: "2020",
        4: "1650",
        3: "1250",
        2: "880",
        1: "500",
    };

    context.set('count', 0); // Reset del contatore

    let urls = configuration.fields.map(field => {
        let activeSensorsCount = Object.values(field.sensors).filter(sensor => sensor).length;
        let height = heightMapping[activeSensorsCount] || "500"; // Default to "500" if no match found

        let updatedUrl = urlTemplate
            .replace("iot-sensors-001", `temporary-iot-sensors-00${field.fieldId}`)
            .replace("iot-sensors-dashboard-field-1", `iot-sensors-dashboard-field-${field.fieldId}`)
            .replace(/height=\d+/, `height=${height}`);

        return updatedUrl;
    });

    // Return the generated URLs for use in Node-RED
    msg.url = urls;
    return msg;
} else {
    context.set('count', count); // Aggiorna il contatore
    return null; // Non inviare nulla
}

