// Funzione per estrarre la parte desiderata dall'URL
function extractDashboardField(responseUrl) {
    // Usa una RegEx per catturare la parte desiderata
    const match = responseUrl.match(/\/d\/[^\/]+\/([^\?]+)/);

    // Se la RegEx trova un match, restituisci il gruppo catturato
    if (match && match[1]) {
        return match[1];
    } else {
        return null; // Nessun match trovato
    }
}
