let configuration = defaultConfiguration;

// Calcola il numero totale di sensori attivi
let totalActiveSensors = 0;

configuration.fields.forEach(field => {
  totalActiveSensors += 1;
});

const catchableErrors = [
  "ENOTFOUND",
  "ECONNRESET",
  "ETIMEDOUT",
  "EHOSTUNREACH",
  "ECONNREFUSED",
  "EAI_AGAIN"
];

if (typeof msg.statusCode !== "string" && !catchableErrors.includes(msg.statusCode)) {

  // Mantieni un contatore nel contesto
  let count = context.get('count') || 0;
  count += 1;

  node.error(totalActiveSensors + " " + count);

  // Controlla se il numero di messaggi ricevuti √® uguale al numero di sensori attivi
  if (count >= totalActiveSensors) {
    context.set('count', 0); // Reset del contatore
    msg.payload = "Done!"
    return msg; // Invia il messaggio

  } else {

    context.set('count', count); // Aggiorna il contatore
    return null; // Non inviare nulla

  }
} else {

  return null;
}
